package variable;

public class DoubleTest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		double dNum = 3.14; //double 타입으로 저장된 숫자가 double 형으로 표현됨
		//float fnNum = 3.14; 3.14는 double 타입으로 저장되기 때문에 오류가 발생 -> 형변환 필요
		
		float fnNum = 3.14F; // 기본적으로 더블을 많이 사용 -> 매개변수를 받을경우 상황에 따라 Float 형 사용
		
		
		

	}

}
